class AudioSplitter:
    def __init__(self, split_parts=3):
        self.split_parts = split_parts
    def split_audio(self, file_path, output_dir):
        """
        Дробит аудиофайл на указанное количество частей и сохраняет их в указанной директории.
        """
        audio = AudioSegment.from_file(file_path)
        duration = len(audio)  # Длительность в миллисекундах
        part_duration = duration // self.split_parts

class FileManager:
    @staticmethod
    def write_text_to_file(text, filename):
        """
        Записывает текст в файл с указанием кодировки utf-8.
        """
        with open(filename, "a", encoding="utf-8") as f:
            f.write(text)

class WhisperTranscriber:
    def __init__(self, device="cuda" if torch.cuda.is_available() else "cpu"):
        self.device = device
        self.model = whisper.load_model("small", device=self.device)
    def transcribe_audio(self, file_name):
        """
        Транскрибирует аудиофайл и возвращает текст.
        """
        result = self.model.transcribe(file_name)
        return result["text"]
